import React, { useState } from 'react';
import { styled } from 'styled-components';
import Timer from './Timer';
import MemoryGame from './MemoryGame';
import ResetBtn from './ResetBtn';

const Wrap = styled.div`
	min-height:100vh;
	padding:0 15px;
	display:flex;
	justify-content:center;
	align-items:center;
	gap:5%;
	background-color:#0a3c2f;
	>div{
		width:100%;
		text-align:center;
	}
`;
const Board = styled.div`
	width:65%;
	max-width:560px;
	padding:40px 60px;
	display:inline-block;
	vertical-align:top;
	border-radius:6px;
	background-color:#0d614b;
	position:relative;
	h1{
		line-height:1.2;
		font-size:3em;
		font-weight:bold;
		color:#fff;
		letter-spacing:0.1em;
	}
`;
const Rank = styled.div`
	width:30%;
	max-width:250px;
	display:inline-block;
	vertical-align:top;
	margin-left:5%;
	padding:10px;
	border-radius:6px;
	background-color:#12614c;
	>h2{
		text-align:center;
		font-size:2.2em;
		font-weight:400;
		color:#fff;
	}
	>ul{
		display:flex;
		flex-direction:column;
		gap:6px;
		margin-top:10px;
		padding:10px;
		text-align:center;
		li{
			padding:5px;
			line-height:1.2;
			font-size:1em;
			border-radius:6px;
			color:#fff;
			background-color:#0c3c2f;
		}
	}
`;
const Save = styled.div`
	width:100%;
	height:100%;
	display:flex;
	justify-content:center;
	align-items:center;
	background-color:rgba(255,255,255,0.3);
	position:fixed;
	inset:0;
	>div{
		width:calc(100% - 60px);
		max-width:400px;
		padding:20px;
		border-radius:6px;
		color:#fff;
		background-color:#042b21;
		h3{
			margin-bottom:20px;
			font-size:2.5em;
			font-weight:bold;
		}
		ul{
			width:50%;
			display:inline-block;
			vertical-align:middle;
			color:#042b21;
			background-color:#fff;
			&:nth-of-type(1){
				border-radius:6px;
			}
			li{
				font-size:1.13em;
				font-weight:bold;
				input{
					width:100%;
					text-align:center;
				}
			}
		}
	}
`;

const Content = () => {
	const [run, setRun] = useState(false);
	const [finish, setFinish] = useState(false);
	const [rank, setRank] = useState("");


	const recordValue = (record) => setRank(record);

	// finish && !run && console.log(rank); //게임 종료시

	return (
		<Wrap>
			<div>
				<Board>
					<h1>같은그림찾기</h1>
					<Timer run={run} setFinish={setFinish} recordValue={recordValue} />
					<MemoryGame setRun={setRun} setFinish={setFinish} />
					<ResetBtn />
				</Board>

				<Rank>
					<h2>순위표</h2>
					<ul>
						<li>1st 닉네임 - {finish && !run ? rank : "00:00"}</li>
						<li>2nd 닉네임 - {finish && !run ? rank : "00:00"}</li>
						<li>3rd 닉네임 - {finish && !run ? rank : "00:00"}</li>
						<li>4th 닉네임 - {finish && !run ? rank : "00:00"}</li>
						<li>5th 닉네임 - {finish && !run ? rank : "00:00"}</li>
					</ul>
				</Rank>

				<Save>
					<div>
						<h3>게임 결과</h3>
						<ul>
							<li>내 기록</li>
							<li>00:00{/* {rank} */}</li>
						</ul>
						<ul>
							<li>이름</li>
							<li><input type="text" placeholder="이름을 입력해주세요." /></li>
						</ul>
					</div>
				</Save>
				{/* {finish && !run &&
					<div>
						<input type="text" />
						{rank}
					</div>
				} */}
			</div>
		</Wrap>
	)
}

export default Content
