import React, {useState, useEffect} from 'react';
import { styled } from 'styled-components';

const Wrap = styled.div`
	width:100%;
	max-width:440px;
	padding-top:100%;
	position:relative;
	>div{
		width:100%;
		height:100%;
		display:flex;
		flex-wrap:wrap;
		gap:3.33%;
		transform-style:preserve-3d;
		perspective:500px;
		position:absolute;
		inset:0;
	}
`;
const Card = styled.div`
	width:22.5%;
	padding-top:22.5%;
	display:flex;
	justify-content:center;
	align-items:center;
	position:relative;
	>div{
		width:100%;
		height:100%;
		display:flex;
		justify-content:center;
		align-items:center;
		font-size:3em;
		background-color:#fff;
		transition:0.25s;
		transform:rotateY(180deg);
		cursor:pointer;
		position:absolute;
		inset:0;
		&:after{
			content:"";
			position:absolute;
			inset:0;
			background-color:#209d70;
			transform:rotateY(0deg);
			backface-visibility:hidden;
		}
		&.boxOpen{
			transform:rotateY(0deg);
			&:after{
				transform: rotateY(180deg);
			}
		}
		&.boxMatch{
			pointer-events:none;
			&:after{
				transform: rotateY(180deg);
			}
		}
	}
`;

const MemoryGame = () => {
	const [randomEmojis, setRandomEmojis] = useState([]);
	const [open, setOpen] = useState([]);
	// const [matchedCards, setMatchedCards] = useState([]);

	useEffect(() => {
		const emojis = ["üòÅ", "üòÅ", "ü§™", "ü§™", "ü•≥", "ü•≥", "üò≠", "üò≠", "ü§¢", "ü§¢", "ü•µ", "ü•µ", "üò±", "üò±", "ü•∂", "ü•∂"];
    const shuffled = emojis.sort(() => (Math.random() > 0.5 ? 1 : -1));
    setRandomEmojis(shuffled);
  }, []);

	const select = (index) => {
		if(open.length >= 2) return; //2Í∞úÏùò Ïπ¥ÎìúÍ∞Ä Ïó¥Î†§ÏûàÏúºÎ©¥ Ïù¥Î≤§Ìä∏ Î¨¥Ïãú
		if(open.includes(index)) return; //Ïù¥ÎØ∏ ÏÑ†ÌÉùÎêú Ïπ¥ÎìúÎ©¥ Ïù¥Î≤§Ìä∏ Î¨¥Ïãú
		setOpen([...open, index]) //ÏÑ†ÌÉùÎêú Ïπ¥Îìú Î∞∞Ïó¥Ïóê Ï∂îÍ∞Ä
	}

	return (
		<Wrap>
			<div>
				{randomEmojis.map((emoji, index) => (
					<Card key={index}>
						<div
							onClick={() => select(index)}
							className={open.includes(index) ? 'boxOpen' : ''}
						>
							{emoji}
						</div>
					</Card>
				))}
			</div>
    </Wrap>
	)
}

export default MemoryGame
