import React, {useState, useEffect} from 'react';
import { styled } from 'styled-components';

const Wrap = styled.div`
	width:100%;
	max-width:440px;
	padding-top:100%;
	position:relative;
	>div{
		width:100%;
		height:100%;
		display:flex;
		flex-wrap:wrap;
		gap:3.33%;
		transform-style:preserve-3d;
		perspective:500px;
		position:absolute;
		inset:0;
	}
`;
const Card = styled.div`
	width:22.5%;
	padding-top:22.5%;
	display:flex;
	justify-content:center;
	align-items:center;
	position:relative;
	>div{
		width:100%;
		height:100%;
		display:flex;
		justify-content:center;
		align-items:center;
		font-size:3em;
		background-color:#fff;
		transition:0.25s;
		transform:rotateY(180deg);
		cursor:pointer;
		position:absolute;
		inset:0;
		&:after{
			content:"";
			position:absolute;
			inset:0;
			background-color:#209d70;
			transform:rotateY(0deg);
			backface-visibility:hidden;
		}
		&.open{
			transform:rotateY(0deg);
			&:after{
				transform: rotateY(180deg);
			}
		}
		&.match{
			pointer-events:none;
			&:after{
				transform: rotateY(180deg);
			}
		}
	}
`;

const MemoryGame = () => {
	const [randomEmojis, setRandomEmojis] = useState([]);
	const [open, setOpen] = useState([]);
	const [match, setMatch] = useState([]);

	useEffect(() => { //ÎûúÎç§ÏàúÏúºÎ°ú Ïù¥Î™®ÏßÄ Ïπ¥Îìú ÎßåÎì§Í∏∞
		const emojis = ["üòÅ", "üòÅ", "ü§™", "ü§™", "ü•≥", "ü•≥", "üò≠", "üò≠", "ü§¢", "ü§¢", "ü•µ", "ü•µ", "üò±", "üò±", "ü•∂", "ü•∂"];
    const shuffled = emojis.sort(() => (Math.random() > 0.5 ? 1 : -1));
    setRandomEmojis(shuffled);
  }, []);

	useEffect(() => {
		if(open.length === 2) {
			const [idx1, idx2] = open;
			const emoji1 = randomEmojis[idx1];
			const emoji2 = randomEmojis[idx2];

			if(emoji1 === emoji2) { //Îëê Ïπ¥ÎìúÏùò Ïù¥Î™®ÏßÄÍ∞Ä ÎèôÏùºÌïú Í≤ΩÏö∞
				setTimeout(() => {
					setMatch([...match, idx1, idx2]);
					setOpen([]);
				}, 500)
			} else{ //Îëê Ïπ¥ÎìúÏùò Ïù¥Î™®ÏßÄÍ∞Ä Îã§Î•∏ Í≤ΩÏö∞
				setTimeout(() => {
					setOpen([]);
				}, 500)
			}
		}

		if(randomEmojis.length > 0 && match.length > 0 && randomEmojis.length === match.length) {
			alert("ÏÑ±Í≥µ");
		}
	}, [open, randomEmojis, match])

	const select = (idx) => { //Ïπ¥Îìú Ïò§Ìîà Ïù¥Î≤§Ìä∏
		if(open.length >= 2) return; //2Í∞úÏùò Ïπ¥ÎìúÍ∞Ä Ïó¥Î†§ÏûàÏúºÎ©¥ Ïù¥Î≤§Ìä∏ Î¨¥Ïãú
		if(open.includes(idx)) return; //Ïù¥ÎØ∏ ÏÑ†ÌÉùÎêú Ïπ¥ÎìúÎ©¥ Ïù¥Î≤§Ìä∏ Î¨¥Ïãú
		setOpen([...open, idx]) //ÏÑ†ÌÉùÎêú Ïπ¥Îìú Î∞∞Ïó¥Ïóê Ï∂îÍ∞Ä
	}

	return (
		<Wrap>
			<div>
				{randomEmojis.map((emoji, idx) => (
					<Card key={idx}>
						<div
							onClick={() => select(idx)}
							className={
								open.includes(idx)
								? "open"
								: match.includes(idx)
								? "match"
								: ""
							}
						>
							{emoji}
						</div>
					</Card>
				))}
			</div>
    </Wrap>
	)
}

export default MemoryGame
