import React, { useState } from 'react';
import { styled } from 'styled-components';
import Timer from './Timer';
import MemoryGame from './MemoryGame';
import ResetBtn from './ResetBtn';

const Wrap = styled.div`
	min-height:100vh;
	display:flex;
	justify-content:center;
	align-items:center;
	background-color:#0a3c2f;
`;
const Board = styled.div`
	width:calc(100% - 40px);
	max-width:560px;
	padding:40px 60px;
	display:flex;
	justify-content:center;
	align-items:center;
	flex-direction:column;
	gap:10px;
	background-color:#0d614b;
	position:relative;
	h1{
		font-size:3em;
		font-weight:bold;
		color:#fff;
		letter-spacing:0.1em;
	}
`;
const Rank = styled.div`

`;

const Content = () => {
	const [run, setRun] = useState(false);
	const [finish, setFinish] = useState(false);
	const [rank, setRank] = useState("");


	const recordValue = (record) => setRank(record);

	finish && !run && console.log(rank); //게임 종료시

	return (
		<Wrap>
			<Board>
				<h1>같은그림찾기</h1>
				<Timer run={run} setFinish={setFinish} recordValue={recordValue} />
				<MemoryGame setRun={setRun} setFinish={setFinish} />
				<ResetBtn />
			</Board>

			<Rank>
				<p>{finish && !run && {rank}}</p>
			</Rank>
		</Wrap>
	)
}

export default Content
