import React, {useState, useEffect} from 'react';
import { styled } from 'styled-components';

const Wrap = styled.div`
	width:100%;
	max-width:440px;
	padding-top:100%;
	position:relative;
	>div{
		width:100%;
		height:100%;
		display:flex;
		flex-wrap:wrap;
		gap:3.33%;
		transform-style:preserve-3d;
		perspective:500px;
		position:absolute;
		inset:0;
	}
`;
const Card = styled.div`
	width:22.5%;
	padding-top:22.5%;
	display:flex;
	justify-content:center;
	align-items:center;
	position:relative;
	>div{
		width:100%;
		height:100%;
		display:flex;
		justify-content:center;
		align-items:center;
		font-size:3em;
		background-color:#fff;
		transition:0.25s;
		transform:rotateY(180deg);
		cursor:pointer;
		position:absolute;
		inset:0;
		&:after{
			content:"";
			position:absolute;
			inset:0;
			background-color:#209d70;
			transform:rotateY(0deg);
			backface-visibility:hidden;
		}
		&.boxOpen{
			transform:rotateY(0deg);
			&:after{
				transform: rotateY(180deg);
			}
		}
		&.boxMatch{
			pointer-events:none;
			&:after{
				transform: rotateY(180deg);
			}
		}
	}
`;

const MemoryGame = () => {
	const emojis = ["😁", "😁", "🤪", "🤪", "🥳", "🥳", "😭", "😭", "🤢", "🤢", "🥵", "🥵", "😱", "😱", "🥶", "🥶"];
	const [randomEmojis, setRandomEmojis] = useState([]);

	useEffect(() => {
    const shuffled = emojis.sort(() => (Math.random() > 0.5 ? 1 : -1));
    setRandomEmojis(shuffled);
  }, []);

	return (
		<Wrap>
			<div>
				{randomEmojis.map((emoji, index) => (
					<Card key={index}>
						<div>
							{emoji}
						</div>
					</Card>
				))}
			</div>
    </Wrap>
	)
}

export default MemoryGame
