import React, {useState, useEffect} from 'react'
import { styled } from 'styled-components'

const Wrap = styled.div`
	width:100%;
	height:100%;
	display:flex;
	flex-wrap:wrap;
	gap:3.33%;
	transform-style:preserve-3d;
	perspective:500px;
	position:absolute;
	inset:0;
`;

const emojis = ["😁", "😁", "🤪", "🤪", "🥳", "🥳", "😭", "😭", "🤢", "🤢", "🥵", "🥵", "😱", "😱", "🥶", "🥶"];

const MemoryGame = () => {
	const [shuffledEmojis, setShuffledEmojis] = useState([]);
  const [isFirstClick, setIsFirstClick] = useState(false);
  const [timer, setTimer] = useState(null);
  const [matchedCount, setMatchedCount] = useState(0);

	useEffect(() => {
    resetGame();
  }, []);

	const shuffleEmojis = () => {
    return emojis.sort(() => (Math.random() > 0.5) ? 1 : -1);
  };

	const resetGame = () => {
    setShuffledEmojis(shuffleEmojis());
    setIsFirstClick(false);
    setMatchedCount(0);
    clearInterval(timer);
  };

	const startTimer = () => {
    const newTimer = setInterval(() => {
    }, 1000);
    setTimer(newTimer);
  };

	const handleBoxClick = (emoji, index) => {
    if (!isFirstClick) {
      startTimer();
      setIsFirstClick(true);
    }

    const boxOpen = document.querySelectorAll(".boxOpen");
    const boxMatch = document.querySelectorAll(".boxMatch");

    if (boxOpen.length === 2) return;

    const newShuffledEmojis = [...shuffledEmojis];
    newShuffledEmojis[index] = emoji;
    setShuffledEmojis(newShuffledEmojis);

    setTimeout(() => {
      if (boxOpen.length > 1) {
        if (boxOpen[0].innerHTML === boxOpen[1].innerHTML) {
          boxOpen[0].classList.add("boxMatch");
          boxOpen[1].classList.add("boxMatch");

          if (boxMatch.length === emojis.length - 2) {
            clearInterval(timer);
            alert("성공!!");
          }
        }

        boxOpen.forEach(box => box.classList.remove("boxOpen"));
      }
    }, 500);
  };

	return (
		<Wrap>
      {shuffledEmojis.map((emoji, index) => (
				<div key={index}>
					<div onClick={() => handleBoxClick(emoji, index)}>
						{emoji}
					</div>
        </div>
      ))}
    </Wrap>
	)
}

export default MemoryGame
